import React, { useState } from "react";
import { render } from "react-dom";
import Timeline from "react-visjs-timeline";
import moment from "moment";
import "./styles.css";

const options = {
  width: "100%",
  height: "90vh",
  showMajorLabels: true,
  showCurrentTime: true,
  zoomMin: 1000000,
  type: "range",
  orientation: "top",
  stack: true,
  editable: true,
};




const groups = [
  {
    id: 1,
    content: "PRODUCT ",
    nestedGroups: [11, 12],
    items: ["€95.00k"],
    showNested: false,
    title:'SDMPre',


  },

  {
    id: 2,
    content: "FLIGHTS",
    items: ["€1000.00k"],
    nestedGroups: [13],
    showNested: false,
    title:'SDMP',

  },

  {
    id: 3,
    content: "TEST-AK",
    items: ["€98.00k"],
    nestedGroups: [14, 15],
    showNested: false,
    title:'SDMP22',


  },
  {
    id: 11,
    content: "PRODUCT QA-1",
    items: ["€101.00k"],
  },
  {
    id: 12,
    content: "PRODUCT QA-2",
    items: ["€102.00k"],
  },
  {
    id: 13,
    content: "FLIGHTS 1",
    items: ["€103.00k"],
  },
  {
    id: 14,
    content: "TEST-AK 1",
    items: ["€104.00k"],
  },
  {
    id: 15,
    content: "TEST-AK 2",
    items: ["€105.00k"],
  },
];


const items = [];
groups.forEach((group) => {
  group.items.forEach((item) => {
    items.push({
      id: items.length,
      start: moment().startOf("day").add(items.length, "days"),
      end: moment()
        .startOf("day")
        .add(items.length + 5, "days"),
      content: item,
      group: group.id,
    });
  });
});

const App = () => {
  const [tooltipContent, setTooltipContent] = useState("");
  const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });

  const handleMouseEnter = (event, content) => {
    setTooltipContent(content);
    setTooltipPosition({ x: event.clientX, y: event.clientY });
  };

  const handleMouseLeave = () => {
    setTooltipContent("");
  };

  const tooltipStyles = {
    position: "fixed",
    top: tooltipPosition.y + 10,
    left: tooltipPosition.x + 10,
    pointerEvents: "none",
    zIndex: 9999,
    backgroundColor: "#fff",
    padding: "5px",
    border: "1px solid #ccc",
    borderRadius: "4px",
    // Add other styling properties as needed (e.g., font size, font color, etc.)
  };

  return (
    <div className="timeline">
      <Timeline
        options={options}
        items={items}
        groups={groups}
        // Add event listeners for tooltip
        clickHandler={(event) => handleMouseEnter(event, "item")}
        onMouseOver={(event, properties) =>
          handleMouseEnter(event, properties.item.content)
        }
        onMouseOut={handleMouseLeave}
      />
      {/* Render the tooltip */}
      {tooltipContent && (
        <div style={tooltipStyles}>
          <div>{tooltipContent}</div>
        </div>
      )}
    </div>
  );
};




