// import React, { useState } from "react";
// import { render } from "react-dom";
// import Timeline from "react-visjs-timeline";
// import moment from "moment";
// import "./styles.css";

const groups = new vis.DataSet([
  { id: 1, content: "Group 1" },
  { id: 2, content: "Group 2" },
]);

const items = new vis.DataSet([
  {
    id: 1,
    group: 1,
    content: "Item 1",
    description: "This is Item 1",
    start: moment("2023-07-21").toDate(),
    end: moment("2023-07-23").toDate(),
  },
  {
    id: 2,
    group: 1,
    content: "Item 2",
    description: "This is Item 2",
    start: moment("2023-07-24").toDate(),
    end: moment("2023-07-26").toDate(),
  },
  {
    id: 3,
    group: 2,
    content: "Item 3",
    description: "This is Item 3",
    start: moment("2023-07-27").toDate(),
    end: moment("2023-07-29").toDate(),
  },
]);

const App = () => {
  const [tooltipContent, setTooltipContent] = useState(null);
  const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });

  const handleMouseOver = (event, properties) => {
    const hoveredItem = items.get(properties.item);
    if (hoveredItem) {
      const { content, description, start, end } = hoveredItem;
      const tooltipText = `Content: ${content}\nDescription: ${description}\nStart Date: ${start.toDateString()}\nEnd Date: ${end.toDateString()}`;
      setTooltipContent(tooltipText);
      setTooltipPosition({ x: event.clientX, y: event.clientY });
    }
  };

  const handleMouseOut = () => {
    setTooltipContent(null);
  };

  return (
    <div className="timeline">
      <Timeline
        options={{
          start: moment().add(-12, "days").toDate(),
          end: moment().add(12, "days").toDate(),
          stack: true,
          showCurrentTime: true,
        }}
        items={items}
        groups={groups}
        clickHandler={() => setTooltipContent(null)}
        onMouseOver={handleMouseOver}
        onMouseOut={handleMouseOut}
      />
      {tooltipContent && (
        <div
          className="tooltip"
          style={{ top: tooltipPosition.y + 10, left: tooltipPosition.x + 10 }}
        >
          <div>{tooltipContent}</div>
        </div>
      )}
    </div>
  );
};

render(<App />, document.getElementById("root"));
